mapper.select:
SELECT review_categrp_no, review_categrp_name, review_categrp_seqno, review_categrp_date
FROM review_categrp
WHERE review_categrp_no = #{review_categrp_no}

mapper.update:
UPDATE review_categrp
SET review_categrp_seqno = review_categrp_seqno - 1
WHERE review_categrp_no = #{review_categrp_no}

UPDATE review_categrp
SET review_categrp_seqno = review_categrp_seqno + 1
WHERE review_categrp_no = #{review_categrp_no}
