mapper.select:
SELECT answer_no, member_no, qna_no, answer_content, answer_rdate, answer_passwd, file1, thumb1, size1
FROM answer
WHERE answer_no = #{answer_no}

mapper.update:
UPDATE answer
SET qna_no= 1, answer_content= #{answer_content} 
WHERE answer_no = #{answer_no}

mapper.select:
SELECT COUNT(*) as passwd_cnt
FROM answer
WHERE answer_no= #{answer_no} AND answer_passwd= #{answer_passwd}

mapper.update:
UPDATE answer
SET file1= #{file1}, thumb1= #{thumb1}, size1= #{size1}
WHERE answer_no= #{answer_no}
